import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Meal = ({
  id,
  title,
  price,
  description,
  popular,
  picture,
  products,
  setProducts,
}) => {
  const handleClick = () => {
    const newProducts = [...products];

    let isFound = false;

    for (let i = 0; i < products.length; i++) {
      if (products[i].id === id) {
        newProducts[i].quantity++;
        isFound = true;
        break;
      }
    }

    if (isFound === false) {
      newProducts.push({
        title: title,
        price: price,
        quantity: 1,
        id: id,
      });
    }
    setProducts(newProducts);
  };

  return (
    <div onClick={handleClick} className="meal-container">
      <div className="meal-infos">
        <div>
          <p>{title}</p>
        </div>
        <p className="meal-infos-description">{description}}</p>
        <div className="meal-horizontal">
          <p>{price} â‚¬</p>
          {popular && (
            <p>
              <FontAwesomeIcon icon="star" />
              Populaire
            </p>
          )}
        </div>
      </div>
      {picture && (
        <img style={{ objectFit: "cover" }} src={picture} alt={title} />
      )}
    </div>
  );
};

export default Meal;
