import { useState, useEffect } from "react";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faStar, faHome } from "@fortawesome/free-solid-svg-icons";

import "./reset.css";
import "./App.css";
import Logo from "./assets/images/logo.png";
import Category from "./components/Category";
library.add(faStar, faHome);

const App = () => {
  const [restaurant, setRestaurant] = useState({});
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [products, setdProducts] = useState([
    { title: "Brunch", price: "25.00", quantity: 1 },
    { title: "Brunch vegan", price: "20.00", quantity: 1 },
  ]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(
        "https://grubux-deliveroo-backend.herokuapp.com/"
      );
      const data = await response.json();
      console.log(data);

      setRestaurant(data.restaurant);
      setCategories(data.categories);
      setIsLoading(false);
    };

    fetchData();
  }, []);
  // const [data, setData] = useState({});
  // const [isLoading, setIsLoading] = useState(true);

  // useEffect(() => {
  //   const fetchData = async () => {
  //     const response = await axios.get(
  //       "https://grubux-deliveroo-backend.herokuapp.com/"
  //     );
  //     setData(response.data);
  //     setIsLoading(false);
  //   };
  //   fetchData();
  // }, []);

  // console.log(data);

  const handleClick = () => {
    const newProducts = [...products];
    newProducts.push({
      title: categories.meals.title,
      price: categories.meals.price,
      quantity: 1,
    });
    setdProducts(newProducts);
  };

  return (
    <>
      {isLoading ? (
        <p>Chargement...</p>
      ) : (
        <div>
          <div>
            <h3>Le Panier</h3>
            {products.map((product, index) => {
              return (
                <div>
                  <button>+</button>
                  {product.quantity}
                  <button>-</button> - {product.title}
                </div>
              );
            })}
          </div>

          <div className="header ">
            <div className="wrapper">
              <img src={Logo} alt="Logo" className="logo" />
            </div>
          </div>

          <div className="wrapper">
            <div className="restaurant-info">
              <div>
                <p>{restaurant.name}</p>
                <p>{restaurant.description}</p>
              </div>

              <img src={restaurant.picture} alt={restaurant.name} />
            </div>
          </div>

          <div className="grey-back">
            <div className="wrapper">
              <div className="restaurant-details">
                <div className="meals">
                  {categories.map((category, index) => {
                    if (category.meals.length === 0) {
                      return null;
                    }

                    return (
                      <Category
                        clic={handleClick}
                        name={category.name}
                        key={index}
                        meals={[...category.meals]}
                      />
                    );
                  })}
                </div>
                <div className="basket">Le panier</div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};
export default App;
