import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Meal = ({
  id,
  title,
  price,
  description,
  popular,
  picture,
  products,
  setProducts,
}) => {
  const handleClick = () => {
    const newProducts = [...products];

    let isFound = false;

    for (let i = 0; i < products.length; i++) {
      if (products[i].id === id) {
        newProducts[i].quantity++;
        isFound = true;
        break;
      }
    }

    if (isFound === false) {
      newProducts.push({
        title: title,
        price: price,
        quantity: 1,
        id: id,
      });
    }
    setProducts(newProducts);
    console.log(products);
  };

  return (
    <div onClick={handleClick} className="meal-container">
      <div className="meal-infos">
        <div key={id}>
          <p>{title}</p>
        </div>
        {description && description.length > 0 && (
          <p className="meal-infos-descriptions">{description}</p>
        )}

        <div className="meal-horizontal">
          <span className="MenuItem--price">
            {Number(price).toFixed(2).replace(".", ",")} â‚¬
          </span>

          {popular && (
            <span className="MenuItem--popular">
              <FontAwesomeIcon
                icon="star"
                size={20}
                style={{ marginRight: 5 }}
              />{" "}
              Populaire
            </span>
          )}

          {popular && (
            <p>
              <FontAwesomeIcon icon="star" />
              Populaire
            </p>
          )}
        </div>
      </div>
      {picture && (
        <img style={{ objectFit: "cover" }} src={picture} alt={title} />
      )}
    </div>
  );
};

export default Meal;
