const Cart = ({ products, setProducts }) => {
  //total
  let total = 0;
  for (let i = 0; i < products.length; i++) {
    total = total + Number(products[i].price) * products[i].quantity;
  }

  const fraisDeLivraison = 2.5;

  let absoluteTotal = total + fraisDeLivraison;

  return (
    <div className="cart">
      <button className={total === 0 ? "cart-button-empty" : "valider"}>
        Valider mon panier
      </button>

      <div className="cart-middle-section">
        <div
          className={total === 0 ? "cart-empty" : "cart-middle-section-full"}
        >
          {total === 0 && <div>Votre panier est vide</div>}
          {products.map((product, index) => {
            const handlePlusClick = () => {
              const newProduct = [...products];
              newProduct[index].quantity++;
              setProducts(newProduct);
            };

            const handleMinusClick = () => {
              const newProduct = [...products];
              if (newProduct[index].quantity === 1) {
                newProduct.splice(index, 1);
              } else {
                newProduct[index].quantity--;
              }
              setProducts(newProduct);
            };

            return (
              <div className="product-global">
                <div className="product-line-main-container">
                  <div className="product-buttons">
                    <button onClick={handleMinusClick}>-</button>
                    {product.quantity}
                    <button onClick={handlePlusClick}> + </button>
                  </div>
                  <div className="product-title">{product.title}</div>
                  <div className="product-price">{product.price} €</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <div className="cart-bottom-section">
        {total === 0 ? null : (
          <div className="result-line">
            <div className="under-total-group">
              <div className="totals-line-main-container">
                <div>Sous-total</div>
                <div>{total} €</div>
              </div>
              <div className="totals-line-main-container">
                <div>Frais de livraison</div>
                <div>{fraisDeLivraison} €</div>
              </div>
            </div>
            <div className="absolute-totals-line-main-container">
              <div>Total</div>
              <div>{absoluteTotal} €</div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
